mapper.update:
UPDATE member
SET member_cookie = #{member_cookie}, member_limit = #{member_limit}
WHERE member_id = #{member_id}

update member
set
	member_pw = #{member_pw}
where
	member_id = #{member_id}

update member
set
	member_email = #{member_email}
where
	member_id = #{member_id}

update member
set
	member_phone = #{member_phone}
where
	member_id = #{member_id}

mapper.select:
SELECT * FROM member 
WHERE 
	member_nick = #{member_nick}
 AND 
 	member_email = #{member_email}

SELECT * FROM member 
WHERE member_id = #{member_id} 
  AND member_nick = #{member_nick} 
  AND member_email = #{member_email}
