mapper.insert:
insert into post (
    post_member_num, post_position_num, post_way_num, post_category_num, 
    post_title, post_content, post_price, post_deal, post_refresh, post_address
) 
values (
    #{post_member_num}, #{post_position_num}, #{post_way_num}, #{post_category_num}, 
    #{post_title}, #{post_content}, #{post_price}, #{post_deal}, #{post_refresh}, #{post_address}
)

mapper.select:
select * from post where post_member_num = #{member_num}
and post_position_num = 1
and post_position_num = 2
and post_position_num = 3
and post_title like concat('%', #{search}, '%') order by post_date desc limit #{pageStart}, #{perPageNum}
