mapper.insert:
insert into post (
    post_member_num, post_position_num, post_way_num, post_category_num, 
    post_title, post_content, post_price, post_deal, post_refresh, post_address
) 
values (
    #{post_member_num}, #{post_position_num}, #{post_way_num}, #{post_category_num}, 
    #{post_title}, #{post_content}, #{post_price}, #{post_deal}, #{post_refresh}, #{post_address}
)
