mapper.insert:
insert into post (
    post_member_num, post_position_num, post_way_num, post_category_num, 
    post_title, post_content, post_price, post_deal, post_address
) 
values (
    #{post_member_num}, #{post_position_num}, #{post_way_num}, #{post_category_num}, 
    #{post_title}, #{post_content}, #{post_price}, #{post_deal}, #{post_address}
)

mapper.select:
select * from post where post_state_num = 1 and post_member_num = #{member_num}
and post_position_num = 1
and post_position_num = 2
and post_position_num = 3
and post_title like concat('%', #{search}, '%') order by post_refresh desc limit #{pageStart}, #{perPageNum}

select * from file where file_target_table = #{target} and file_target_num = #{post_num}
order by file_num
limit 0, 1

mapper.insert:
insert into after (
    after_member_num, after_review1, after_review2, after_review3, after_message
) 
values (
    #{after_member_num}, #{after_review1}, #{after_review2}, #{after_review3}, #{after_message}
)
